        elseif  ( trigger == "MouseLeave"     ) then table.setAt ( privateVariables, 1, "MouseLeave"  ) table.setAt ( privateVariables, 2, nil                ) user.sendEvent ( application.getCurrentUser ( ), "HLDMain", "onRegisterObjectEvent", this.getObject ( ), "HLDSwitchScene", "MouseLeave" )
        elseif  ( trigger == "MouseClick"     ) then table.setAt ( privateVariables, 1, "MouseClick"  ) table.setAt ( privateVariables, 2, 0                  ) user.sendEvent ( application.getCurrentUser ( ), "HLDMain", "onRegisterObjectEvent", this.getObject ( ), "HLDSwitchScene", "MouseClick" )
        end
    end
	
